import React from "react";
import "./styles.scss";
const CATEGORIES = [
  { name: "Accounting / Finance", icon: "Shape2.png" },
  { name: "Design / Creative", icon: "Shape3.png" },
  { name: "Progammer", icon: "Shape4.png" },
  { name: "Production / Operation", icon: "Shape5.png" },
  { name: "Education / Training", icon: "Shape6.png" },
  { name: " Consultancy", icon: "Shape7.png" }
];

export function Categories() {
  return (
    <div className="categories">
      <h1 className="categories--title">{"Browse jobs by category"}</h1>
      <p className="categories--description">
        {"Choose from the list of most popular categories"}
      </p>
      <div className="list">
        {CATEGORIES.map(el => (
          <div className="list--item">
            <img
              lassName="list--img"
              src={require("../../assets/images/" + el.icon)}
              alt=""
            />
            <p lassName="list--name">{el.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
